				);
				return (
					date.getDate() +
					' ' +
					getMonthText(date.getMonth()) +
					' ' +
					getYearText(date.getFullYear())
				);
			}
			if (positionX === 1) {
				const date = new Date(
					new Date(today).setDate(today.getDate() - 60),
